import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../../App';
import Footer from '../../Footer';
import { useNavigate } from 'react-router-dom';
import Navbar from '../../Navbar';
import './Registration-Form.css';
import swal from 'sweetalert';


function Registration_Form() {
  const [user, setUser] = useState({
    firstname: '',
    lastname: '',
    birthid: '',
    nid: '',
    hid:'',
    dateofbirth: '',
    phonenumber: '',
    fathersname: '',
    fathersnid: '',
    mothersname: '',
    mothersnid: '',
    presentaddress: '',
    permanentaddress: '',
    password: '',
    confirmpassword: '',
    gender: '',
    emergphonenumber: '',
    })

    const [formErrors , setFormErrors] =useState({});
    const [isSubmit , setIsSubmit] =useState(false);

  function handleChange (e) {
    const {name, value} = e.target;
    setUser(prevUser => {
      return{
      ...prevUser,
      [name]: value 
    }
    })

  }

  function handleClick(event) {
    event.preventDefault();

    const healthid = user.birthid;

    const healthid1 = healthid.slice(0,4);
    const healthid2 = healthid.slice(11);

    const userhid = healthid1+healthid2;

    user.hid = userhid;

    setFormErrors(validate(user));

    setIsSubmit(true); 

  }

  let navigate = useNavigate();

  useEffect(()=> {

    if(Object.keys(formErrors).length === 0 && isSubmit){

      const newClient = {
        firstname: user.firstname,
        lastname: user.lastname,
        birthid: user.birthid,
        nid: user.nid,
        hid: user.hid,
        dateofbirth: user.dateofbirth,
        phonenumber: user.phonenumber,
        fathersname: user.fathersname,
        fathersnid: user.fathersnid,
        mothersname: user.mothersname,
        mothersnid: user.mothersnid,
        presentaddress: user.presentaddress,
        permanentaddress: user.permanentaddress,
        password: user.password,
        confirmpassword: user.confirmpassword,
        gender: user.gender,
        emergphonenumber: user.emergphonenumber
        }

        if(user.firstname && user.lastname && user.birthid && user.nid && user.dateofbirth && user.hid && user.fathersname && user.mothersname && user.fathersnid && user.mothersnid && user.gender && user.password && user.confirmpassword && user.presentaddress && user.permanentaddress && user.emergphonenumber ){
          swal({
            title: "Registration Done!",
            text: "Now, Please login!",
            icon: "success",
            button: "Okay",
          }).then((done) => {
            if (done) {

              navigate("/login_form_client");
            }
          });
          axios.post('http://localhost:3001/create1', newClient)
          .then( res => console.log(res))
        }

    }
 },[formErrors])

 const validate = (values) =>{

  const errors = {};

  var nameRegex = /^[a-zA-Z ]{2,30}$/;
  var numReg = /^\d+$/;
  var phoneReg = /^(?:\+88|88)?(01[3-9]\d{8})$/;

  if(!values.firstname){
