import { createStore } from "redux";

const initialState = {
    users: [],
    loggedInUser: null,
    products:[{
         id : 1,
         name : 'Pen',
         price : 5
    }]
}

const reducer = (state = initialState  , action) =>{
    switch (action.type){
         case "REGISTER":
            return{
                ...state,
                users : [...state.users , action.payload]
            }

            default:
                return state;
    }
}

export default createStore(reducer)
