import React, {useState} from 'react';
import '../../../App';
import { Link, useNavigate } from 'react-router-dom';
import Footer from '../../Footer';
import Navbar from '../../Navbar';
import './Login-Form.css'
import axios from 'axios';
import swal from 'sweetalert';

const Login_Form_Client = (props) => {

 const navigate = useNavigate();

  const [user, setUser] = useState({
    hid: "",
    password: "",
    })

  const handleChange = e =>{
    const {name, value} = e.target
    setUser({
      ...user,
      [name]: value 
    })

  }

  var result = "";
  var userInfo="";

  function handleClick(event) {

    event.preventDefault();

    axios.post('http://localhost:3001/clientLogin', user)
    .then(res => {
      result = (res.data.message)

      if(result == "logindone"){

            navigate("/profile",{
              state:{
                user: res.data.user,
              }
            });

            props.setCheckUser(res.data.users)

            userInfo =(res.data.users.firstname)

            sessionStorage.setItem("UserInfo", JSON.stringify(userInfo))

            const storeobj = {
                type: "Client",
                login: true
            }

            props.updateUser(storeobj)

      }
      else if(result == "passwordisIncorrect"){
        swal({
          title: "Password is Invalid!",
          text: "Please, Check again",
          icon: "warning",
          button: "Okay",
        });
      }
      else if(result == "userDoesNotExist"){
        swal({
          title: "User Does Not Exist!",
          text: "Please, Register First",
          icon: "warning",
          button: "Okay",
        });
      }
    })
  }

  return (
    <>
      <div className='bgg'>
    <Navbar
      NavPage="other"/>

    <div className="center">
      <h1>Login As Client</h1>
      <form>
        <div className="txt_field">
          <input type="text" name="hid" value={user.hid} onChange={handleChange} required/>
          <span></span>
          <label>Enter Health ID</label>
        </div>
        <div className="txt_field">
          <input type="password" name="password" value={user.password} onChange={handleChange} required/>
          <span></span>
          <label>Enter Password</label>
        </div>

        <input type="submit" onClick={handleClick} value="Login"/>
        <div className="signup_link">
        Not Registered? <Link to='/registration'>Register Here</Link>
        </div>
      </form>
    </div>
    <Footer/>
    </div>

    </>
  );
}

export default Login_Form_Client;
